import React, { useState, useEffect } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import Header from '../components/Header';
import Footer from '../components/Footer';
import '../styles.css';

const ReviewerSignup = () => {
  const [formData, setFormData] = useState({
    name: '',
    availability: '',
    discordHandle: ''
  });
  const [submitted, setSubmitted] = useState(false);
  const [loading, setLoading] = useState(true);
  const location = useLocation();
  const navigate = useNavigate();

  useEffect(() => {
    const searchParams = new URLSearchParams(location.search);
    const code = searchParams.get('code');

    if (code) {
      fetchGitHubData(code);
    } else {
      navigate('/reviewer-signup');
    }
  }, [location, navigate]);

  const fetchGitHubData = async (code) => {
    try {
      setLoading(true);
      const response = await fetch('/api/github-auth', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ code }),
      });
      const data = await response.json();
      setFormData(prevData => ({
        ...prevData,
        name: data.name || '',
      }));
    } catch (error) {
      console.error('Error fetching GitHub data:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await fetch('/api/reviewer-signup', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(formData)
      });
      const data = await response.json();
      if (response.ok) {
        setSubmitted(true);
      } else {
        console.error('Submission failed:', response.status, data);
      }
    } catch (error) {
      console.error('Error:', error);
    }
  };

  const handleNavigateToDiscord = () => {
    window.open('https://discord.gg/m2Qud5GDqp', '_blank');
  };

  if (loading) {
    return <div>Loading...</div>;
  }

  if (submitted) {
    return (
      <div>
        <Header />
        <div className="container">
          <h2>Thank you for applying!</h2>
          <p>Thanks for applying to become a Code Reviewer at BasedAgent. A member of our team will be in touch to review your application and reach out to you if there is a fit.</p>
          <button type="button" onClick={handleNavigateToDiscord}>Join Our Discord</button>
        </div>
        <Footer />
      </div>
    );
  }

  return (
    <div>
      <Header />
      <div className="container">
        <h1>Complete Your Reviewer Profile</h1>
        <form onSubmit={handleSubmit} className="reviewer-form">
          <input 
            type="text" 
            name="name" 
            placeholder="Name" 
            value={formData.name}
            onChange={handleChange} 
            required 
          />
          <input 
            type="text" 
            name="availability" 
            placeholder="Availability (hours per week)" 
            value={formData.availability}
            onChange={handleChange} 
            required 
          />
          <input 
            type="text" 
            name="discordHandle" 
            placeholder="Discord Handle" 
            value={formData.discordHandle}
            onChange={handleChange} 
            required 
          />
          <button type="submit">Submit Application</button>
        </form>
      </div>
      <Footer />
    </div>
  );
};

export default ReviewerSignup;
