// ReviewerSignup.js

import React, { useState, useEffect } from 'react';
import { useLocation } from 'react-router-dom';
import Header from '../components/Header';
import Footer from '../components/Footer';
import '../styles.css';

const ReviewerSignup = () => {
  const [formData, setFormData] = useState({
    name: '',
    github_username: '',
    github_url: '',
    top_languages: [],
    total_contributions: 0,
    public_repositories: 0,
    skills: '',
    availability: '',
    email: '',
    discordHandle: ''
  });
  const [submitted, setSubmitted] = useState(false);
  const location = useLocation();

  useEffect(() => {
    const params = new URLSearchParams(location.search);
    const githubData = Object.fromEntries(params);

    if (githubData.github_username) {
      setFormData(prevData => ({
        ...prevData,
        ...githubData,
        top_languages: githubData.top_languages ? githubData.top_languages.split(',') : [],
        total_contributions: Number(githubData.total_contributions),
        public_repositories: Number(githubData.public_repositories),
        skills: githubData.top_languages ? githubData.top_languages.replace(/,/g, ', ') : ''
      }));
    }
  }, [location]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prevData => ({
      ...prevData,
      [name]: name === 'top_languages' ? value.split(',').map(lang => lang.trim()) : value
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await fetch('/api/reviewer-signup', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          ...formData,
          top_languages: formData.top_languages.join(', ')
        })
      });
      const data = await response.json();
      if (response.ok) {
        setSubmitted(true);
      } else {
        console.error('Submission failed:', response.status, data);
      }
    } catch (error) {
      console.error('Error:', error);
    }
  };

  const handleNavigateToDiscord = () => {
    window.open('https://discord.gg/m2Qud5GDqp', '_blank');
  };

  if (submitted) {
    return (
      <div>
        <Header />
        <div className="container">
          <h2>Thank you for applying!</h2>
          <p>Thanks for applying to become a Code Reviewer at BasedAgent. A member of our team will be in touch to review your application and reach out to you if there is a fit.</p>
          <button type="button" onClick={handleNavigateToDiscord}>Join Our Discord</button>
        </div>
        <Footer />
      </div>
    );
  }

  return (
    <div>
      <Header />
      <div className="container">
        <h1>Complete Your Reviewer Profile</h1>
        <form onSubmit={handleSubmit} className="reviewer-form">
          <input 
            type="text" 
            name="name" 
            placeholder="Name" 
            value={formData.name}
            onChange={handleChange} 
            required 
          />
          <input 
            type="text" 
            name="github_username" 
            placeholder="GitHub Username" 
            value={formData.github_username}
            readOnly 
          />
          <input 
            type="text" 
            name="github_url" 
            placeholder="GitHub URL" 
            value={formData.github_url}
            readOnly 
          />
          <input 
            type="text" 
            name="top_languages" 
            placeholder="Top Languages" 
            value={formData.top_languages.join(', ')}
            onChange={handleChange} 
            required 
          />
          <input 
            type="number" 
            name="total_contributions" 
            placeholder="Total Contributions" 
            value={formData.total_contributions}
            readOnly 
          />
          <input 
            type="number" 
            name="public_repositories" 
            placeholder="Public Repositories" 
            value={formData.public_repositories}
            readOnly 
          />
          <textarea 
            name="skills" 
            placeholder="Skills (e.g., Python, TensorFlow, Solidity)" 
            value={formData.skills}
            onChange={handleChange} 
            required
          ></textarea>
          <input 
            type="text" 
            name="availability" 
            placeholder="Availability (hours per week)" 
            value={formData.availability}
            onChange={handleChange} 
            required 
          />
          <input 
            type="email" 
            name="email" 
            placeholder="Email" 
            value={formData.email}
            onChange={handleChange} 
            required 
          />
          <input 
            type="text" 
            name="discordHandle" 
            placeholder="Discord Handle" 
            value={formData.discordHandle}
            onChange={handleChange} 
            required 
          />
          <button type="submit">Submit Application</button>
        </form>
      </div>
      <Footer />
    </div>
  );
};

export default ReviewerSignup;
